CREATE TABLE IF NOT EXISTS appointments(
   appointment_id   VARCHAR(4) NOT NULL PRIMARY KEY
  ,patient_id       VARCHAR(4) NOT NULL
  ,doctor_id        VARCHAR(4) NOT NULL
  ,appointment_date DATE  NOT NULL
  ,appointment_time VARCHAR(8) NOT NULL
  ,reason_for_visit VARCHAR(12) NOT NULL
  ,status           VARCHAR(9) NOT NULL
);


CREATE TABLE IF NOT EXISTS billing(
   bill_id        VARCHAR(4) NOT NULL PRIMARY KEY
  ,patient_id     VARCHAR(4) NOT NULL
  ,treatment_id   VARCHAR(4) NOT NULL
  ,bill_date      DATE  NOT NULL
  ,amount         NUMERIC(7,2) NOT NULL
  ,payment_method VARCHAR(11) NOT NULL
  ,payment_status VARCHAR(7) NOT NULL
);


CREATE TABLE IF NOT EXISTS doctors(
   doctor_id        VARCHAR(4) NOT NULL PRIMARY KEY
  ,first_name       VARCHAR(6) NOT NULL
  ,last_name        VARCHAR(6) NOT NULL
  ,specialization   VARCHAR(11) NOT NULL
  ,phone_number     INTEGER  NOT NULL
  ,years_experience INTEGER  NOT NULL
  ,hospital_branch  VARCHAR(16) NOT NULL
  ,email            VARCHAR(28) NOT NULL
);

CREATE TABLE IF NOT EXISTS patients(
   patient_id         VARCHAR(4) NOT NULL PRIMARY KEY
  ,first_name         VARCHAR(7) NOT NULL
  ,last_name          VARCHAR(8) NOT NULL
  ,gender             VARCHAR(1) NOT NULL
  ,date_of_birth      DATE  NOT NULL
  ,contact_number     INTEGER  NOT NULL
  ,address            VARCHAR(12) NOT NULL
  ,registration_date  DATE  NOT NULL
  ,insurance_provider VARCHAR(12) NOT NULL
  ,insurance_number   VARCHAR(9) NOT NULL
  ,email              VARCHAR(24) NOT NULL
);

CREATE TABLE IF NOT EXISTS treatments(
   treatment_id   VARCHAR(4) NOT NULL PRIMARY KEY
  ,appointment_id VARCHAR(4) NOT NULL
  ,treatment_type VARCHAR(13) NOT NULL
  ,description    VARCHAR(18) NOT NULL
  ,cost           NUMERIC(7,2) NOT NULL
  ,treatment_date DATE  NOT NULL
);








